mapper.select:
SELECT

FROM code a

WHERE a.id = #{id}

SELECT

FROM code a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM code a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE code SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
